import React, { useState } from "react";
import { ethers } from "ethers";
import SimpleStorage_abi from "./contracts/SimpleStorage_abi.json";

import classes from "./SignDoc.module.css";
import Button from "../../components/Button/Button";

const SimpleStorage = () => {
  // deploy simple storage contract and paste deployed contract address here. This value is local ganache chain
  let contractAddress = "0x24509385D9070687b1a816858D26Ae610053fd91"; //"0xB31dF1dac0BE3024AD5CC6b0298F347511F9e4F7";

  const [errorMessage, setErrorMessage] = useState(null);
  const [defaultAccount, setDefaultAccount] = useState(null);
  const [connButtonText, setConnButtonText] = useState("Connect Wallet");

  const [currentContractVal, setCurrentContractVal] = useState(false);

  const [provider, setProvider] = useState(null);
  const [signer, setSigner] = useState(null);
  const [contract, setContract] = useState(null);

  const [option, setOptionNum] = useState(0);
  const [computerOption, setOptionCompNum] = useState(0);
  const [whiteList, setWhiteList] = useState([]);
  const connectWalletHandler = () => {
    if (window.ethereum && window.ethereum.isMetaMask) {
      window.ethereum
        .request({ method: "eth_requestAccounts" })
        .then((result) => {
          accountChangedHandler(result[0]);
          setConnButtonText("Wallet Connected");
        })
        .catch((error) => {
          setErrorMessage(error.message);
        });
    } else {
      console.log("Need to install MetaMask");
      setErrorMessage("Please install MetaMask browser extension to interact");
    }
  };

  // update account, will cause component re-render
  const accountChangedHandler = (newAccount) => {
    setDefaultAccount(newAccount);
    updateEthers();
  };

  const chainChangedHandler = () => {
    // reload the page to avoid any errors with chain change mid use of application
    window.location.reload();
    // console.log("hehe");
  };

  // listen for account changes
  window.ethereum.on("accountsChanged", accountChangedHandler);

  window.ethereum.on("chainChanged", chainChangedHandler);

  const updateEthers = () => {
    let tempProvider = new ethers.providers.Web3Provider(window.ethereum, 97);
    setProvider(tempProvider);

    let tempSigner = tempProvider.getSigner();
    setSigner(tempSigner);

    let tempContract = new ethers.Contract(
      contractAddress,
      SimpleStorage_abi,
      tempSigner
    );

    setContract(tempContract);
    console.log(contract);
  };

  const playGame = async (event) => {
    event.preventDefault();

    let option = parseInt(event.target.attributes.values.value);

    if (contract != null) {
      const txResponse = await contract.requestRandomNum();
      //   const txReceipt = txResponse.wait(1);

      console.log(txResponse);
      const res = await contract.playGame(option);

      const isWin = await contract.getResult();
      console.log(res.Promise);
      if (isWin && res != null) {
        setOptionNum(option + 1);
      } else {
        setOptionCompNum(computerOption + 1);
      }

      //   console.log(res);
      //   getOption();
    }
  };

  //   return (
  //     <div>
  //       <h4> {"Get/Set Contract interaction"} </h4>
  //       <button onClick={connectWalletHandler}>{connButtonText}</button>
  //       <div>
  //         <h3>Address: {defaultAccount}</h3>
  //       </div>
  //       <form onSubmit={setHandler}>
  //         <input id="setText" type="text" />
  //         <button type={"submit"}> Update Contract </button>
  //       </form>
  //       <div>
  //         <button onClick={getCurrentVal} style={{ marginTop: "5em" }}>
  //           {" "}
  //           Get Current Contract Value{" "}
  //         </button>
  //       </div>
  //       {currentContractVal}
  //       {errorMessage}
  //     </div>
  //   );
  return (
    <div className={classes.Container}>
      <div className={classes.blockWallet}>
        <button onClick={connectWalletHandler} className={classes.btnWallet}>
          {connButtonText}
        </button>
        <h5> {defaultAccount}</h5>
      </div>
      <div className={classes.Main}>
        <div className={classes.blockCenter}>
          <h1>Договор подписи </h1>

          <div className={classes.blockSigner}>
            <h6 id="resOptionComp"> Подписанты:</h6>
            {/* <h6>{computerOption}</h6> */}
            {/* <h6 id="resOption">{option}</h6> */}
          </div>
          <div className={classes.blockChoice}></div>
        </div>
        <Button type="submit" onClick={() => {}} value="Подписать"></Button>
      </div>
    </div>
  );
};

export default SimpleStorage;
