import classes from "./MainPage.module.css";
import Button from "../../components/Button/Button.js";
import ButtonChoice from "../../components/Button/ButtonChoice.js";

import myOwnScript from "./script.js";
const contractAddress = "0x7f10b6c77d8104EC0a9D94ed2410f5360B131180"; //Замените вашим контрактом

// Указываем ABI (Application Binary Interface) контракта
const abi = [
  {
    inputs: [],
    stateMutability: "payable",
    type: "constructor",
  },
  {
    anonymous: false,
    inputs: [
      {
        indexed: false,
        internalType: "address",
        name: "player",
        type: "address",
      },
      {
        indexed: false,
        internalType: "bool",
        name: "isWinner",
        type: "bool",
      },
      {
        indexed: false,
        internalType: "string",
        name: "mssg",
        type: "string",
      },
    ],
    name: "GamePlayed",
    type: "event",
  },
  {
    inputs: [
      {
        internalType: "uint8",
        name: "_option",
        type: "uint8",
      },
    ],
    name: "playGame",
    outputs: [
      {
        internalType: "bool",
        name: "",
        type: "bool",
      },
    ],
    stateMutability: "payable",
    type: "function",
  },
  {
    stateMutability: "payable",
    type: "receive",
  },
];

// Подключаемся к web3 провайдеру (метамаск)
const provider = new ethers.providers.Web3Provider(window.ethereum, 97);

let signer;
let contract;

//Запрашиваем аккаунты пользователя и подключаемся к первому аккаунту
provider.send("eth_requestAccounts", []).then(() => {
  provider.listAccounts().then((accounts) => {
    signer = provider.getSigner(accounts[0]);
    //Создаем объект контракта
    contract = new ethers.Contract(contractAddress, abi, signer);
    console.log(contract);
  });
});

//Вызываем playGame() в смарт-контракте
async function playGame(option) {
  const result = await contract.playGame(option);
  console.log(result);
  if (result) {
  } else {
  }
}

export function MainPage() {
  return (
    <div className={classes.Main}>
      <div className={classes.blockCenter}>
        <h1>Гоу в камень ножницы бумага!</h1>

        <div className={classes.blockResult}>
          <h6 id="resOptionComp"></h6>
          <br />
          <h6>:</h6>
          <br />
          <h6 id="resOption"></h6>
        </div>
        <div className={classes.blockChoice}>
          <a
            href=""
            className={classes.choice}
            value="1"
            onClick={myOwnScript.playGame(1)}
          >
            <img src="images/fist.png" className={classes.imgChoice}></img>
          </a>

          <a
            href=""
            className={classes.choice}
            value="2"
            onClick={myOwnScript.playGame(2)}
          >
            <img src="images/scissors.png" className={classes.imgChoice}></img>
          </a>
          <a
            href=""
            className={classes.choice}
            value="3"
            onClick={myOwnScript.playGame(3)}
          >
            <img src="images/hello.png" className={classes.imgChoice}></img>
          </a>
        </div>
      </div>

      <Button type="submit" onClick={() => {}} value="Играть"></Button>
    </div>
  );
}
